var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

isDatePast: function(strDate) {
	// Create GlideDateTime objects for the current and the passed in dates
	var rightNow = new GlideDateTime();
	var testDate = new GlideDateTime(strDate);

	//If the test date is before rightNow, return true, else return false
	if (testDate.before(rightNow)) {
		return true;
	} else {
		return false;
	}
},

isDateToday: function(strDate) {
	// Create GlideDateTime objects for the current and the passed in dates
	var aaj = (new GlideDateTime()).getLocalDate();
	var testDate = (new GlideDateTime(strDate)).getLocalDate();

	//If the test date is today, return true, else return false
	if (aaj.compareTo(testDate) == 0) {
		var x;
		return true;
	} else {
		var y;
		return false;
	}
},

    type: 'NeedItUtils'
};
